/**
 * View ajax
 */
setPageContentView = function (uri, params, type = 'POST', dataType = 'json') {
    $.ajax({
        url: uri,
        data: params,
        method: type,
        dataType: dataType,
        cache: false,
        contentType: false,
        processData: false,
        success: function (response) {
            return response;
        }
    });
};
